INSERT INTO T_FILE(ID, IMAGE_TYPE, FILE_NAME, REMARK, CREATED_BY, CREATED_AT, MODIFIED_BY
, MODIFIED_AT, GROUP_NAME, FAST_NAME)

  SELECT #{item.id,mode=IN,jdbcType=DECIMAL}
  , #{item.imageType,mode=IN, jdbcType=VARCHAR}
  , #{item.fileName,mode=IN, jdbcType=VARCHAR}
  , #{item.remark, mode=IN,jdbcType=VARCHAR}
  , #{item.createdBy, mode=IN,jdbcType=VARCHAR}
  , #{item.createdAt,mode=IN, jdbcType=DECIMAL}
  , #{item.modifiedBy,mode=IN, jdbcType=VARCHAR}
  , #{item.modifiedAt, mode=IN,jdbcType=DECIMAL}
  , #{item.groupName,mode=IN, jdbcType=DECIMAL}
  , #{item.fastName, mode=IN,jdbcType=DECIMAL} FROM dual
